import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';
import { useNavigate } from 'react-router-dom';
import './GyroscopeTest.css';

function GyroscopeTest() {
  const containerRef = useRef();
  const navigate = useNavigate();
  const [isGyroscopeSupported, setIsGyroscopeSupported] = useState(false);
  const [isGyroscopeActive, setIsGyroscopeActive] = useState(false);
  const [permissionGranted, setPermissionGranted] = useState(false);
  const [error, setError] = useState(null);
  
  const sceneRef = useRef();
  const cameraRef = useRef();
  const rendererRef = useRef();
  const controlsRef = useRef();
  const animationIdRef = useRef();

  useEffect(() => {
    // Check if device orientation is supported
    if (window.DeviceOrientationEvent) {
      setIsGyroscopeSupported(true);
    }

    // Initialize Three.js scene
    initScene();

    // Cleanup on unmount
    return () => {
      if (animationIdRef.current) {
        cancelAnimationFrame(animationIdRef.current);
      }
      if (rendererRef.current) {
        rendererRef.current.dispose();
      }
      // Remove device orientation listener
      window.removeEventListener('deviceorientation', handleDeviceOrientation);
    };
  }, []);

  const initScene = () => {
    if (!containerRef.current) return;

    // Clear container
    while (containerRef.current.firstChild) {
      containerRef.current.removeChild(containerRef.current.firstChild);
    }

    // Create scene
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Create camera
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 0.1);
    cameraRef.current = camera;

    // Create renderer
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(window.devicePixelRatio);
    rendererRef.current = renderer;
    containerRef.current.appendChild(renderer.domElement);

    // Create sphere geometry for 360¬∞ image
    const geometry = new THREE.SphereGeometry(500, 128, 96);
    geometry.scale(-1, 1, 1); // Invert the sphere so the image is on the inside

    // Load the brandshop1.jpg image
    const textureLoader = new THREE.TextureLoader();
    textureLoader.load('/brandshop1.jpg', (texture) => {
      texture.minFilter = THREE.LinearFilter;
      texture.magFilter = THREE.LinearFilter;
      if (texture.colorSpace !== undefined && THREE.LinearSRGBColorSpace) {
        texture.colorSpace = THREE.LinearSRGBColorSpace;
      }

      const material = new THREE.MeshBasicMaterial({
        map: texture,
        side: THREE.DoubleSide
      });

      const sphere = new THREE.Mesh(geometry, material);
      scene.add(sphere);
    }, undefined, (error) => {
      console.error('Error loading texture:', error);
      setError('Error al cargar la imagen 360¬∞');
    });

    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleResize);

    // Animation loop
    const animate = () => {
      animationIdRef.current = requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };
    animate();

    // Cleanup resize listener
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  };

  const handleDeviceOrientation = (event) => {
    if (!isGyroscopeActive || !cameraRef.current) return;

    // Get device orientation data
    const alpha = event.alpha; // Z-axis rotation (0-360)
    const beta = event.beta;   // X-axis rotation (-180 to 180)
    const gamma = event.gamma; // Y-axis rotation (-90 to 90)

    if (alpha !== null && beta !== null && gamma !== null) {
      // Convert device orientation to camera rotation
      const radius = 500;
      
      // Convert degrees to radians
      const alphaRad = THREE.MathUtils.degToRad(alpha);
      const betaRad = THREE.MathUtils.degToRad(beta);
      const gammaRad = THREE.MathUtils.degToRad(gamma);

      // Calculate position based on device orientation
      const x = radius * Math.sin(betaRad) * Math.sin(alphaRad);
      const y = radius * Math.cos(betaRad);
      const z = radius * Math.sin(betaRad) * Math.cos(alphaRad);

      // Smooth camera movement
      cameraRef.current.position.lerp(new THREE.Vector3(x, y, z), 0.1);
      cameraRef.current.lookAt(0, 0, 0);
    }
  };

  const requestGyroscopePermission = async () => {
    try {
      // Request permission for device orientation
      if (typeof DeviceOrientationEvent.requestPermission === 'function') {
        const permission = await DeviceOrientationEvent.requestPermission();
        if (permission === 'granted') {
          setPermissionGranted(true);
          setIsGyroscopeActive(true);
          window.addEventListener('deviceorientation', handleDeviceOrientation);
        } else {
          setError('Permiso denegado para acceder al giroscopio');
        }
      } else {
        // For devices that don't require permission
        setPermissionGranted(true);
        setIsGyroscopeActive(true);
        window.addEventListener('deviceorientation', handleDeviceOrientation);
      }
    } catch (error) {
      console.error('Error requesting gyroscope permission:', error);
      setError('Error al solicitar permiso del giroscopio');
    }
  };

  const toggleGyroscope = () => {
    if (isGyroscopeActive) {
      setIsGyroscopeActive(false);
      window.removeEventListener('deviceorientation', handleDeviceOrientation);
    } else {
      if (permissionGranted) {
        setIsGyroscopeActive(true);
        window.addEventListener('deviceorientation', handleDeviceOrientation);
      } else {
        requestGyroscopePermission();
      }
    }
  };

  const handleMouseMove = (event) => {
    if (isGyroscopeActive) return; // Don't handle mouse when gyroscope is active

    const rect = containerRef.current.getBoundingClientRect();
    const mouseX = ((event.clientX - rect.left) / rect.width) * 2 - 1;
    const mouseY = -((event.clientY - rect.top) / rect.height) * 2 + 1;

    const radius = 500;
    const x = radius * Math.sin(mouseY * Math.PI) * Math.sin(mouseX * Math.PI);
    const y = radius * Math.cos(mouseY * Math.PI);
    const z = radius * Math.sin(mouseY * Math.PI) * Math.cos(mouseX * Math.PI);

    cameraRef.current.position.lerp(new THREE.Vector3(x, y, z), 0.1);
    cameraRef.current.lookAt(0, 0, 0);
  };

  return (
    <div className="gyroscope-test-container">
      <div className="gyroscope-header">
        <button className="back-btn" onClick={() => navigate('/hub')}>
          ‚Üê Volver al Hub
        </button>
        <h1>Test de Giroscopio 360¬∞</h1>
        <div className="gyroscope-controls">
          {isGyroscopeSupported ? (
            <button 
              className={`gyroscope-btn ${isGyroscopeActive ? 'active' : ''}`}
              onClick={toggleGyroscope}
            >
              {isGyroscopeActive ? 'üîÑ Giroscopio Activo' : 'üì± Activar Giroscopio'}
            </button>
          ) : (
            <span className="not-supported">‚ùå Giroscopio no soportado</span>
          )}
        </div>
      </div>

      {error && (
        <div className="error-message">
          <span>‚ö†Ô∏è</span> {error}
        </div>
      )}

      <div className="instructions">
        <div className="instruction-card">
          <h3>üì± En M√≥vil:</h3>
          <p>‚Ä¢ Toca "Activar Giroscopio" para usar el sensor del dispositivo</p>
          <p>‚Ä¢ Mueve tu tel√©fono para explorar la imagen 360¬∞</p>
          <p>‚Ä¢ Funciona como los videos 360¬∞ de YouTube</p>
        </div>
        <div className="instruction-card">
          <h3>üñ±Ô∏è En Desktop:</h3>
          <p>‚Ä¢ Mueve el mouse para explorar la imagen</p>
          <p>‚Ä¢ Usa la rueda del mouse para hacer zoom</p>
        </div>
      </div>

      <div 
        ref={containerRef} 
        className="viewer-container"
        onMouseMove={handleMouseMove}
      />
    </div>
  );
}

export default GyroscopeTest;
