import * as THREE from 'three';

export default function RadialFadeMaterial(prevTexture, nextTexture, progress) {
  return new THREE.ShaderMaterial({
    uniforms: {
      uPrev: { value: prevTexture },
      uNext: { value: nextTexture },
      uProgress: { value: progress },
      uResolution: { value: new THREE.Vector2(1, 1) },
    },
    vertexShader: `
      varying vec2 vUv;
      void main() {
        vUv = uv;
        gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
      }
    `,
    fragmentShader: `
      uniform sampler2D uPrev;
      uniform sampler2D uNext;
      uniform float uProgress;
      varying vec2 vUv;
      
      // Función de easing suave para transiciones profesionales
      float smoothEase(float t) {
        return t * t * (3.0 - 2.0 * t);
      }
      
      // Función de easing más suave para el fade
      float smoothFade(float t) {
        return t * t * t * (t * (t * 6.0 - 15.0) + 10.0);
      }
      
      void main() {
        vec2 center = vec2(0.5, 0.5);
        
        // Aplicar easing suave al progreso
        float easedProgress = smoothEase(uProgress);
        
        // Fade out de la imagen actual con timing más suave
        float currentImageFade = 1.0 - smoothFade(clamp(easedProgress * 1.2, 0.0, 1.0));
        
        // Fade in de la nueva imagen con timing más suave
        float newImageFade = smoothFade(clamp((easedProgress - 0.2) * 1.25, 0.0, 1.0));
        
        // Efecto de desplazamiento sutil y suave
        float displacementStrength = 0.15; // Reducido para ser más sutil
        vec2 offset = (vUv - center) * (1.0 + easedProgress * displacementStrength);
        vec2 newUv = center + offset;
        
        // Verificar límites de coordenadas UV
        if (newUv.x < 0.0 || newUv.x > 1.0 || newUv.y < 0.0 || newUv.y > 1.0) {
          newUv = vUv;
        }
        
        // Muestrear texturas
        vec4 prevColor = texture2D(uPrev, vUv);
        vec4 nextColor = texture2D(uNext, newUv);
        
        // Blending más suave con crossfade
        float blendFactor = smoothstep(0.0, 1.0, easedProgress);
        float finalBlend = mix(currentImageFade, newImageFade, blendFactor);
        
        // Aplicar transición con blending suave
        gl_FragColor = mix(prevColor, nextColor, finalBlend);
      }
    `,
    transparent: false,
    depthTest: false,
    depthWrite: false,
  });
}
