import React, { useEffect, useState } from 'react';
import './Shop.css';

function Shop() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Reemplaza la URL por tu endpoint real de productos
    fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/products`)
      .then(res => res.json())
      .then(data => {
        setProducts(data);
        setLoading(false);
      });
  }, []);

  const handleQuantityChange = (productId, value) => {
    setCart(prev => ({
      ...prev,
      [productId]: {
        ...prev[productId],
        quantity: Math.max(1, Number(value) || 1)
      }
    }));
  };

  const handleAddToCart = (product) => {
    setCart(prev => ({
      ...prev,
      [product._id]: {
        ...product,
        quantity: prev[product._id]?.quantity || 1
      }
    }));
  };

  if (loading) return <div className="shop-loading">Cargando productos...</div>;

  return (
    <div className="shop-container">
      <h1 className="shop-title">Tienda Virtual</h1>
      <div className="shop-grid">
        {products.map(product => (
          <div className="shop-card" key={product._id}>
            <div className="shop-card-image">
              <img src={product.image} alt={product.name} />
            </div>
            <div className="shop-card-content">
              <h2 className="shop-card-title">{product.name}</h2>
              <p className="shop-card-desc">{product.description}</p>
              <div className="shop-card-price">${product.price.toFixed(2)}</div>
              <div className="shop-card-actions">
                <input
                  type="number"
                  min="1"
                  className="shop-qty-input"
                  value={cart[product._id]?.quantity || 1}
                  onChange={e => handleQuantityChange(product._id, e.target.value)}
                />
                <button
                  className="shop-add-btn"
                  onClick={() => handleAddToCart(product)}
                >
                  AÃ±adir al carrito
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
      {/* Carrito flotante (opcional, mejora UX) */}
      <div className="shop-cart-float">
        <span>ðŸ›’</span>
        <span>
          {Object.values(cart).reduce((acc, item) => acc + (item.quantity || 1), 0)} productos
        </span>
      </div>
    </div>
  );
}

export default Shop;
