// Hub main view component
import React, { useState, useEffect } from 'react';
import TourCard from './TourCard';
import api from '../../services/api';
import './Hub.css';

function Hub() {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const data = await api.getTours();
        setTours(data);
        setLoading(false);
      } catch (err) {
        setError('Error al cargar los tours');
        setLoading(false);
        console.error(err);
      }
    };
    
    fetchTours();
  }, []);

  if (loading) {
    return <div className="loading">Cargando tours...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="hub-container">
      <header className="hub-header">
        <h1>Tours 360° Manager</h1>
        <p>Crea y gestiona experiencias inmersivas en 360 grados</p>
      </header>

      <div className="features-section">
        <div className="feature-card">
          <div className="feature-icon">🖼️</div>
          <h3>Sube Imágenes</h3>
          <p>Arrastra y suelta imágenes 360° para crear tours inmersivos</p>
        </div>
        
        <div className="feature-card">
          <div className="feature-icon">👁️</div>
          <h3>Vista Previa</h3>
          <p>Visualiza los tours en tiempo real mientras los editas</p>
        </div>
        
        <div className="feature-card">
          <div className="feature-icon">⚙️</div>
          <h3>Gestiona Tours</h3>
          <p>Organiza, edita y elimina los tours de manera sencilla</p>
        </div>
      </div>

      <section className="my-tours-section">
        <h2>Mis Tours</h2>
        
        {tours.length === 0 ? (
          <div className="empty-tours">
            <p>No tienes ningún tour creado aún</p>
            <button className="create-tour-btn">Crear mi primer tour</button>
          </div>
        ) : (
          <div className="tours-grid">
            {tours.map(tour => (
              <TourCard key={tour._id} tour={tour} />
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

export default Hub;
