import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import { useNavigate } from 'react-router-dom';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import api from '../services/api';

// Fix default icon issue with Leaflet in React
import iconUrl from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
let DefaultIcon = L.icon({
  iconUrl,
  shadowUrl: iconShadow,
  iconAnchor: [12, 41],
});
L.Marker.prototype.options.icon = DefaultIcon;

function LandingMap() {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const response = await api.getTours();
        setTours(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error al cargar los tours');
        setLoading(false);
      }
    };
    fetchTours();
  }, []);

  if (loading) {
    return <div style={{ color: '#38bdf8', textAlign: 'center', marginTop: 40 }}>Cargando mapa...</div>;
  }
  if (error) {
    return <div style={{ color: 'red', textAlign: 'center', marginTop: 40 }}>{error}</div>;
  }

  // Centrar el mapa en la media de los puntos o en una ubicación por defecto
  const defaultPosition = tours.length > 0
    ? [
        tours.reduce((sum, t) => sum + (t.localizacion?.lat || 0), 0) / tours.length,
        tours.reduce((sum, t) => sum + (t.localizacion?.lng || 0), 0) / tours.length
      ]
    : [20, -75]; // Cuba por defecto

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <MapContainer center={defaultPosition} zoom={6} style={{ width: '100%', height: '100%' }}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {tours.filter(t => t.localizacion).map(tour => (
          <Marker
            key={tour._id}
            position={[tour.localizacion.lat, tour.localizacion.lng]}
            eventHandlers={{
              dblclick: () => navigate(`/viewer/${tour._id}`)
            }}
          >
            <Popup>
              <strong>{tour.name}</strong><br />
              {tour.description}<br />
              <span style={{ color: '#38bdf8', cursor: 'pointer' }} onClick={() => navigate(`/viewer/${tour._id}`)}>
                Ver tour 360
              </span>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}

export default LandingMap;
