import React, { useRef, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import './GyroscopeTest.css';

function GyroscopeTest() {
  const navigate = useNavigate();
  const containerRef = useRef();
  const cameraRef = useRef();
  const sceneRef = useRef();
  const rendererRef = useRef();
  const controlsRef = useRef();
  const [isMobile, setIsMobile] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);
  const [deviceOrientation, setDeviceOrientation] = useState({ alpha: 0, beta: 0, gamma: 0 });

  useEffect(() => {
    // Detectar si es dispositivo móvil
    const checkMobile = () => {
      const userAgent = navigator.userAgent || navigator.vendor || window.opera;
      const mobileRegex = /android|webos|iphone|ipad|ipod|blackberry|iemobile|opera mini/i;
      const isMobileDevice = mobileRegex.test(userAgent);
      setIsMobile(isMobileDevice);
      return isMobileDevice;
    };

    const mobile = checkMobile();

    // Configuración de Three.js
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 0.1);
    cameraRef.current = camera;
    sceneRef.current = scene;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.setSize(window.innerWidth, window.innerHeight);
    rendererRef.current = renderer;
    
    if (containerRef.current) {
      containerRef.current.appendChild(renderer.domElement);
    }

    // Controles de órbita para desktop
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.enableZoom = true;
    controls.minDistance = 0.05;
    controls.maxDistance = 2.5;
    controlsRef.current = controls;

    // Cargar imagen 360
    const geometry = new THREE.SphereGeometry(500, 128, 96);
    geometry.scale(-1, 1, 1);

    const textureLoader = new THREE.TextureLoader();
    
    // Función para crear textura de fallback
    const createFallbackTexture = () => {
      const canvas = document.createElement('canvas');
      canvas.width = 1024;
      canvas.height = 512;
      const ctx = canvas.getContext('2d');
      
      // Crear un patrón de gradiente para simular una imagen 360
      const gradient = ctx.createLinearGradient(0, 0, canvas.width, 0);
      gradient.addColorStop(0, '#1e40af');
      gradient.addColorStop(0.25, '#38bdf8');
      gradient.addColorStop(0.5, '#818cf8');
      gradient.addColorStop(0.75, '#38bdf8');
      gradient.addColorStop(1, '#1e40af');
      
      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      
      // Agregar texto indicativo
      ctx.fillStyle = 'white';
      ctx.font = '48px Arial';
      ctx.textAlign = 'center';
      ctx.fillText('Vista de Prueba 360°', canvas.width / 2, canvas.height / 2);
      ctx.font = '24px Arial';
      ctx.fillText('Giroscopio Funcionando', canvas.width / 2, canvas.height / 2 + 40);
      
      const fallbackTexture = new THREE.CanvasTexture(canvas);
      fallbackTexture.minFilter = THREE.LinearFilter;
      fallbackTexture.magFilter = THREE.LinearFilter;
      
      return fallbackTexture;
    };

    // Función para cargar imagen
    const loadImage = () => {
      textureLoader.load(
        '/brandshop1.jpg',
        texture => {
          console.log('Imagen cargada exitosamente');
          texture.minFilter = THREE.LinearFilter;
          texture.magFilter = THREE.LinearFilter;
          texture.generateMipmaps = false;
          
          if (texture.colorSpace !== undefined && THREE.LinearSRGBColorSpace) {
            texture.colorSpace = THREE.LinearSRGBColorSpace;
          }
          
          const material = new THREE.MeshBasicMaterial({
            map: texture,
            side: THREE.DoubleSide
          });
          
          const sphere = new THREE.Mesh(geometry, material);
          scene.add(sphere);
          setImageLoaded(true);
          setImageError(false);
        },
        (progress) => {
          console.log('Progreso de carga:', (progress.loaded / progress.total * 100).toFixed(2) + '%');
        },
        error => {
          console.error('Error al cargar la imagen:', error);
          
          const fallbackTexture = createFallbackTexture();
          const material = new THREE.MeshBasicMaterial({
            map: fallbackTexture,
            side: THREE.DoubleSide
          });
          
          const sphere = new THREE.Mesh(geometry, material);
          scene.add(sphere);
          setImageLoaded(true);
          setImageError(true);
        }
      );
    };

    // Cargar imagen con timeout para móviles
    if (mobile) {
      const timeout = setTimeout(() => {
        if (!imageLoaded) {
          console.log('Timeout alcanzado, usando fallback');
          const fallbackTexture = createFallbackTexture();
          const material = new THREE.MeshBasicMaterial({
            map: fallbackTexture,
            side: THREE.DoubleSide
          });
          
          const sphere = new THREE.Mesh(geometry, material);
          scene.add(sphere);
          setImageLoaded(true);
          setImageError(true);
        }
      }, 8000);
      
      loadImage();
      
      return () => clearTimeout(timeout);
    } else {
      loadImage();
    }

    // Manejar eventos de orientación del dispositivo
    const handleDeviceOrientation = (event) => {
      if (mobile && event) {
        setDeviceOrientation({
          alpha: event.alpha || 0,
          beta: event.beta || 0,
          gamma: event.gamma || 0
        });
      }
    };

    // Función de animación
    const animate = () => {
      requestAnimationFrame(animate);
      
      if (mobile && camera) {
        // Usar datos del giroscopio para rotar la cámara
        const { alpha, beta, gamma } = deviceOrientation;
        
        if (alpha !== null && beta !== null && gamma !== null) {
          // Convertir grados a radianes
          const alphaRad = THREE.MathUtils.degToRad(alpha);
          const betaRad = THREE.MathUtils.degToRad(beta);
          const gammaRad = THREE.MathUtils.degToRad(gamma);
          
          // Aplicar rotación basada en el giroscopio con suavizado
          const targetRotationX = betaRad - Math.PI / 2;
          const targetRotationY = alphaRad;
          const targetRotationZ = gammaRad;
          
          // Suavizar la rotación
          camera.rotation.x += (targetRotationX - camera.rotation.x) * 0.1;
          camera.rotation.y += (targetRotationY - camera.rotation.y) * 0.1;
          camera.rotation.z += (targetRotationZ - camera.rotation.z) * 0.1;
        }
      } else if (controls) {
        // Usar controles de órbita para desktop
        controls.update();
      }
      
      if (renderer && scene && camera) {
        renderer.render(scene, camera);
      }
    };
    animate();

    // Manejar cambio de tamaño de ventana
    const handleResize = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;
      
      if (camera) {
        camera.aspect = width / height;
        camera.updateProjectionMatrix();
      }
      
      if (renderer) {
        renderer.setSize(width, height);
      }
    };

    window.addEventListener('resize', handleResize);

    // Agregar listener de orientación del dispositivo para móviles
    if (mobile && 'DeviceOrientationEvent' in window) {
      window.addEventListener('deviceorientation', handleDeviceOrientation);
    }

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('deviceorientation', handleDeviceOrientation);
      
      if (containerRef.current && renderer && renderer.domElement) {
        try {
          containerRef.current.removeChild(renderer.domElement);
        } catch (e) {
          console.log('Error al limpiar renderer:', e);
        }
      }
      
      if (renderer) {
        renderer.dispose();
      }
    };
  }, [isMobile, deviceOrientation]);

  return (
    <div className="gyroscope-test-container">
      <button className="back-button" onClick={() => navigate('/hub')}>
        ← Volver al Hub
      </button>
      
      <div className="gyroscope-header">
        <h2>Prueba de Giroscopio</h2>
        <div className="device-info">
          <p>Dispositivo: {isMobile ? 'Móvil' : 'Desktop'}</p>
          <p>Giroscopio: {isMobile ? 'Activado' : 'Controles de Mouse'}</p>
          {isMobile && (
            <div className="orientation-data">
              <p>Alpha: {deviceOrientation.alpha?.toFixed(2) || '0.00'}°</p>
              <p>Beta: {deviceOrientation.beta?.toFixed(2) || '0.00'}°</p>
              <p>Gamma: {deviceOrientation.gamma?.toFixed(2) || '0.00'}°</p>
            </div>
          )}
        </div>
      </div>
      
      <div 
        ref={containerRef} 
        className="gyroscope-viewer"
        style={{ 
          width: '100%', 
          height: 'calc(100vh - 200px)',
          position: 'relative',
          overflow: 'hidden'
        }}
      >
        {!imageLoaded && !imageError && (
          <div className="loading-indicator">
            <div className="spinner"></div>
            <p>Cargando imagen 360°...</p>
          </div>
        )}
        
        {imageError && (
          <div className="error-message">
            <h3>⚠️ Vista de Prueba</h3>
            <p>Mostrando textura de prueba. El giroscopio funciona perfectamente.</p>
          </div>
        )}
      </div>
      
      <div className="gyroscope-instructions">
        {isMobile ? (
          <div>
            <h3>Instrucciones para móvil:</h3>
            <p>• Mueve tu dispositivo para explorar la vista 360°</p>
            <p>• El movimiento del teléfono se refleja en tiempo real</p>
            <p>• El giroscopio está activado automáticamente</p>
          </div>
        ) : (
          <div>
            <h3>Instrucciones para desktop:</h3>
            <p>• Usa el mouse para arrastrar y explorar la vista 360°</p>
            <p>• Usa la rueda del mouse para hacer zoom</p>
            <p>• Prueba esta función en un dispositivo móvil para el giroscopio</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default GyroscopeTest;
