import React, { useState, useEffect } from 'react';
import './HotspotCreationModal.css';

// Iconos sugeridos para hotspots (puedes cambiar URLs por los tuyos)
const HOTSPOT_ICONS = [
  { id: 'arrow', url: 'https://cdn-icons-png.flaticon.com/512/54/54215.png', name: 'Flecha' },
  { id: 'info', url: 'https://cdn-icons-png.flaticon.com/512/1828/1828884.png', name: 'Información' },
  { id: 'shop', url: 'https://cdn-icons-png.flaticon.com/512/891/891462.png', name: 'Tienda' },
  { id: 'food', url: 'https://cdn-icons-png.flaticon.com/512/706/706164.png', name: 'Comida' },
  { id: 'wc', url: 'https://cdn-icons-png.flaticon.com/512/809/809052.png', name: 'Baño' },
  { id: 'exit', url: 'https://cdn-icons-png.flaticon.com/512/1828/1828778.png', name: 'Salida' },
  { id: 'star', url: 'https://cdn-icons-png.flaticon.com/512/2107/2107957.png', name: 'Destacado' },
  { id: 'heart', url: 'https://cdn-icons-png.flaticon.com/512/2107/2107955.png', name: 'Favorito' },
];

function HotspotCreationModal({ position, tour, currentSceneIndex, onSave, onCancel }) {
  const [hotspotType, setHotspotType] = useState('access');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [targetSceneId, setTargetSceneId] = useState('');
  const [selectedImage, setSelectedImage] = useState('');
  const [socialMedia, setSocialMedia] = useState({
    facebook: '',
    instagram: '',
    twitter: '',
    website: ''
  });

  // Filtrar escenas disponibles (excluyendo la actual)
  const availableScenes = tour.scenes.filter((scene, idx) => idx !== currentSceneIndex);

  useEffect(() => {
    if (hotspotType === 'access' && HOTSPOT_ICONS.length > 0 && !selectedImage) {
      setSelectedImage(HOTSPOT_ICONS[0].url);
    }
  }, [hotspotType, selectedImage]);

  const handleSocialMediaChange = (e) => {
    const { name, value } = e.target;
    setSocialMedia(prev => ({ ...prev, [name]: value }));
  };

  const handleImageSelect = (url) => {
    setSelectedImage(url);
  };

  const handleSceneSelect = (sceneId) => {
    setTargetSceneId(sceneId);
  };

  const handleSubmit = () => {
    const hotspotData = {
      type: hotspotType,
      pitch: position.pitch,
      yaw: position.yaw,
      title,
      description
    };

    if (hotspotType === 'access') {
      hotspotData.targetSceneId = targetSceneId;
      hotspotData.imageUrl = selectedImage;
    } else if (hotspotType === 'commerce') {
      hotspotData.socialMedia = socialMedia;
    }

    onSave(hotspotData);
  };

  return (
    <div className="modal-overlay">
      <div className="hotspot-creation-modal dark-theme">
        <div className="modal-header">
          <h3>Crear Nuevo Hotspot</h3>
          <button className="close-btn" onClick={onCancel}>×</button>
        </div>
        <div className="modal-body">
          <div className="form-group">
            <label>Tipo de Hotspot</label>
            <select 
              value={hotspotType} 
              onChange={e => {
                setHotspotType(e.target.value);
                setTargetSceneId('');
                setSelectedImage('');
              }}
              className="type-selector"
            >
              <option value="access">Punto de Acceso</option>
              <option value="commerce">Comercio</option>
              <option value="location">Locación</option>
            </select>
          </div>
          <div className="form-group">
            <label>Título *</label>
            <input 
              type="text" 
              value={title} 
              onChange={e => setTitle(e.target.value)} 
              placeholder="Nombre del hotspot"
              required
              className="form-input"
            />
          </div>
          <div className="form-group">
            <label>Descripción</label>
            <textarea 
              value={description} 
              onChange={e => setDescription(e.target.value)} 
              placeholder="Descripción detallada"
              rows="2"
              className="form-textarea"
            />
          </div>
          {hotspotType === 'access' && (
            <>
              <div className="form-group">
                <label>Seleccionar escena destino</label>
                {availableScenes.length === 0 ? (
                  <div className="no-scenes">
                    <p>No hay otras escenas disponibles en este tour.</p>
                    <p>Primero crea más escenas para poder enlazarlas.</p>
                  </div>
                ) : (
                  <div className="scenes-grid">
                    {availableScenes.map((scene, idx) => (
                      <div 
                        key={scene._id || idx}
                        className={`scene-item${targetSceneId === (scene._id || idx) ? ' selected' : ''}`}
                        onClick={() => handleSceneSelect(scene._id || idx)}
                      >
                        {scene.image ? (
                          <img 
                            src={scene.image} 
                            alt={scene.name || `Escena ${idx + 1}`} 
                            className="scene-thumb"
                            onError={e => e.target.src = 'default-image.jpg'}
                          />
                        ) : (
                          <div className="scene-thumb placeholder">
                            <span>🖼️</span>
                          </div>
                        )}
                        <div className="scene-name">
                          {scene.name || `Escena ${idx + 1}`}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
              {targetSceneId && (
                <div className="selected-scene-preview">
                  <div>Escena seleccionada:</div>
                  <div className="preview-info">
                    {availableScenes.find(s => (s._id || s.id) === targetSceneId)?.name || 'Escena destino'}
                  </div>
                </div>
              )}
            </>
          )}
          {hotspotType === 'commerce' && (
            <div className="social-media-fields">
              <div className="form-group">
                <label>Facebook</label>
                <input 
                  type="url" 
                  name="facebook" 
                  value={socialMedia.facebook} 
                  onChange={handleSocialMediaChange} 
                  placeholder="https://facebook.com/perfil"
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label>Instagram</label>
                <input 
                  type="url" 
                  name="instagram" 
                  value={socialMedia.instagram} 
                  onChange={handleSocialMediaChange} 
                  placeholder="https://instagram.com/perfil"
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label>Twitter</label>
                <input 
                  type="url" 
                  name="twitter" 
                  value={socialMedia.twitter} 
                  onChange={handleSocialMediaChange} 
                  placeholder="https://twitter.com/perfil"
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label>Sitio Web</label>
                <input 
                  type="url" 
                  name="website" 
                  value={socialMedia.website} 
                  onChange={handleSocialMediaChange} 
                  placeholder="https://sitio.com"
                  className="form-input"
                />
              </div>
            </div>
          )}
        </div>
        <div className="modal-footer">
          <button className="btn-cancel" onClick={onCancel}>Cancelar</button>
          <button 
            className="btn-save"
            onClick={handleSubmit}
            disabled={!title || (hotspotType === 'access' && !targetSceneId)}
          >
            Guardar Hotspot
          </button>
        </div>
      </div>
    </div>
  );
}

export default HotspotCreationModal;
