  const startTransition = (targetIdx) => {
    if (transitioning || navigationInProgress) {
      console.log('Navegaci贸n ya en progreso, ignorando clic');
      return;
    }

    const targetScene = tour.scenes[targetIdx];
    if (!targetScene) {
      console.error('Escena destino no encontrada');
      return;
