        rendererRef.current.domElement.width,
        rendererRef.current.domElement.height
      );
      rendererRef.current.setRenderTarget(renderTarget);
      rendererRef.current.render(sceneRef.current, cameraRef.current);
      rendererRef.current.setRenderTarget(null);
      setPrevTexture(renderTarget.texture);
    }
  };

  useEffect(() => {
    if (!tour || !tour.scenes || tour.scenes.length === 0) return;

