import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../../services/api';
import './Shop.css';

function Shop({ tourId }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [tour, setTour] = useState(null);
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();
  const id = tourId || useParams().tourId;

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        // Obtener productos y tour
        const tourRes = await api.getTour(id);
        setTour(tourRes.data);
        // Suponiendo que hay un endpoint para productos por tour
        const prodRes = await api.get(`/products/by-tour/${id}`);
        setProducts(prodRes.data.products || []);
      } catch (err) {
        setProducts([]);
      }
      setLoading(false);
    }
    if (id) fetchData();
  }, [id]);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  return (
    <div className="shop-container">
      <button className="shop-back-btn" onClick={() => navigate('/hub')}>‚Üê Volver al Hub</button>
      <div className="shop-title">{tour?.name || 'Tienda'}</div>
      {loading ? (
        <div className="shop-loading">Cargando productos...</div>
      ) : (
        <div className="shop-grid">
          {products.length === 0 ? (
            <div style={{ color: '#a3a3a3', fontSize: '1.1rem', gridColumn: '1/-1', textAlign: 'center', padding: 32 }}>
              No hay productos en esta tienda.
            </div>
          ) : (
            products.map(product => (
              <div className="shop-card" key={product._id}>
                <div className="shop-card-image">
                  <img
                    src={product.image}
                    alt={product.name}
                    onError={e => { e.target.src = 'https://via.placeholder.com/300x180?text=Sin+Imagen'; }}
                  />
                </div>
                <div className="shop-card-content">
                  <div className="shop-card-title">{product.name}</div>
                  <div className="shop-card-desc">{product.description}</div>
                  <div className="shop-card-price">${product.price}</div>
                  <div className="shop-card-actions">
                    <button className="shop-add-btn" onClick={() => addToCart(product)}>
                      <span role="img" aria-label="carrito">üõí</span> Agregar
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      )}
      {cart.length > 0 && (
        <div className="shop-cart-float">
          <span>üõí {cart.length} producto(s)</span>
          <button className="shop-add-btn" onClick={() => alert('Ver carrito')}>
            Ver Carrito
          </button>
        </div>
      )}
    </div>
  );
}

export default Shop;
