import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as THREE from 'three';
import './GyroscopeTest.css';

const GyroscopeTest = () => {
  const canvasRef = useRef(null);
  const navigate = useNavigate();
  const [isGyroscopeSupported, setIsGyroscopeSupported] = useState(false);
  const [isGyroscopeActive, setIsGyroscopeActive] = useState(false);
  const [error, setError] = useState(null);

  // Variables para el giroscopio
  const gyroscopeRef = useRef(null);
  const animationRef = useRef(null);
  const rotationRef = useRef({ x: 0, y: 0, z: 0 });
  
  // Variables para controles de mouse/touch
  const isMouseDownRef = useRef(false);
  const mousePositionRef = useRef({ x: 0, y: 0 });
  const lastMousePositionRef = useRef({ x: 0, y: 0 });

  // Variables para el renderizado
  const sceneRef = useRef(null);
  const cameraRef = useRef(null);
  const rendererRef = useRef(null);
  const sphereRef = useRef(null);

  // Variables para el giroscopio mejorado
  const initialOrientationRef = useRef(null);
  const currentOrientationRef = useRef({ alpha: 0, beta: 0, gamma: 0 });

  useEffect(() => {
    // Verificar soporte del giroscopio
    if ('DeviceOrientationEvent' in window) {
      setIsGyroscopeSupported(true);
      initializeScene();
    } else {
      setError('Tu dispositivo no soporta el giroscopio');
      initializeScene(); // Inicializar de todas formas para mouse/touch
    }

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      if (gyroscopeRef.current) {
        window.removeEventListener('deviceorientation', gyroscopeRef.current);
      }
    };
  }, []);

  const initializeScene = () => {
    const canvas = canvasRef.current;
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Configurar c√°mara
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 0.1);
    cameraRef.current = camera;

    // Configurar renderer
    const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    rendererRef.current = renderer;

    // Crear esfera con la imagen 360¬∞
    const geometry = new THREE.SphereGeometry(500, 60, 40);
    geometry.scale(-1, 1, 1); // Invertir la geometr√≠a para ver la imagen desde dentro

    // Cargar la imagen local brandshop1.jpg
    const textureLoader = new THREE.TextureLoader();
    textureLoader.load(
      '/brandshop1.jpg',
      (texture) => {
        texture.mapping = THREE.EquirectangularReflectionMapping;
        const material = new THREE.MeshBasicMaterial({ map: texture });
        const sphere = new THREE.Mesh(geometry, material);
        scene.add(sphere);
        sphereRef.current = sphere;
        animate();
      },
      undefined,
      (error) => {
        console.error('Error cargando la imagen:', error);
        setError('Error al cargar la imagen 360¬∞ de la tienda');
      }
    );

    // Manejar redimensionamiento
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', handleResize);

    // Agregar controles de mouse/touch (solo cuando el giroscopio no est√° activo)
    const canvasElement = canvasRef.current;
    
    const handleMouseDown = (event) => {
      if (isGyroscopeActive) return; // No permitir mouse/touch cuando giroscopio est√° activo
      isMouseDownRef.current = true;
      const rect = canvasElement.getBoundingClientRect();
      lastMousePositionRef.current = {
        x: event.clientX - rect.left,
        y: event.clientY - rect.top
      };
    };

    const handleMouseMove = (event) => {
      if (!isMouseDownRef.current || isGyroscopeActive) return;
      
      const rect = canvasElement.getBoundingClientRect();
      mousePositionRef.current = {
        x: event.clientX - rect.left,
        y: event.clientY - rect.top
      };

      const deltaX = mousePositionRef.current.x - lastMousePositionRef.current.x;
      const deltaY = mousePositionRef.current.y - lastMousePositionRef.current.y;

      const sensitivity = 0.005;
      rotationRef.current.y += deltaX * sensitivity;
      rotationRef.current.x += deltaY * sensitivity;

      lastMousePositionRef.current = { ...mousePositionRef.current };
    };

    const handleMouseUp = () => {
      isMouseDownRef.current = false;
    };

    // Eventos de mouse
    canvasElement.addEventListener('mousedown', handleMouseDown);
    canvasElement.addEventListener('mousemove', handleMouseMove);
    canvasElement.addEventListener('mouseup', handleMouseUp);
    canvasElement.addEventListener('mouseleave', handleMouseUp);

    // Eventos de touch
    canvasElement.addEventListener('touchstart', (event) => {
      if (isGyroscopeActive) {
        event.preventDefault();
        return;
      }
      event.preventDefault();
      const touch = event.touches[0];
      handleMouseDown(touch);
    });

    canvasElement.addEventListener('touchmove', (event) => {
      if (isGyroscopeActive) {
        event.preventDefault();
        return;
      }
      event.preventDefault();
      const touch = event.touches[0];
      handleMouseMove(touch);
    });

    canvasElement.addEventListener('touchend', (event) => {
      event.preventDefault();
      handleMouseUp();
    });
  };

  const animate = () => {
    animationRef.current = requestAnimationFrame(animate);
    
    if (sphereRef.current) {
      // Aplicar rotaci√≥n siempre
      sphereRef.current.rotation.x = rotationRef.current.x;
      sphereRef.current.rotation.y = rotationRef.current.y;
    }
    
    if (rendererRef.current && sceneRef.current && cameraRef.current) {
      rendererRef.current.render(sceneRef.current, cameraRef.current);
    }
  };

  const startGyroscope = async () => {
    try {
      // Intentar solicitar permisos si es necesario
      if ('DeviceOrientationEvent' in window && 'requestPermission' in DeviceOrientationEvent) {
        try {
          const permission = await DeviceOrientationEvent.requestPermission();
          if (permission !== 'granted') {
            setError('Permiso denegado para acceder al giroscopio');
            return;
          }
        } catch (err) {
          console.log('No se requieren permisos expl√≠citos');
        }
      }

      const handleGyroscope = (event) => {
        if (event.alpha !== null && event.beta !== null && event.gamma !== null) {
          // Guardar orientaci√≥n inicial si es la primera vez
          if (!initialOrientationRef.current) {
            initialOrientationRef.current = {
              alpha: event.alpha,
              beta: event.beta,
              gamma: event.gamma
            };
            console.log('Orientaci√≥n inicial establecida:', initialOrientationRef.current);
          }

          // Calcular la diferencia desde la orientaci√≥n inicial
          const deltaAlpha = event.alpha - initialOrientationRef.current.alpha;
          const deltaBeta = event.beta - initialOrientationRef.current.beta;
          const deltaGamma = event.gamma - initialOrientationRef.current.gamma;

          // Convertir a radianes y aplicar sensibilidad
          const sensitivity = 0.02;
          
          // Rotaci√≥n Y (horizontal) basada en alpha (rotaci√≥n del dispositivo)
          rotationRef.current.y = THREE.MathUtils.degToRad(deltaAlpha) * sensitivity;
          
          // Rotaci√≥n X (vertical) basada en beta (inclinaci√≥n hacia adelante/atr√°s)
          rotationRef.current.x = THREE.MathUtils.degToRad(deltaBeta - 90) * sensitivity;

          console.log('Gyroscope data:', {
            alpha: event.alpha,
            beta: event.beta,
            gamma: event.gamma,
            deltaAlpha,
            deltaBeta,
            rotationX: rotationRef.current.x,
            rotationY: rotationRef.current.y
          });
        }
      };

      gyroscopeRef.current = handleGyroscope;
      window.addEventListener('deviceorientation', handleGyroscope);
      setIsGyroscopeActive(true);
      setError(null);
      
      // Resetear orientaci√≥n inicial
      initialOrientationRef.current = null;
      
    } catch (error) {
      console.error('Error starting gyroscope:', error);
      setError('Error al activar el giroscopio');
    }
  };

  const stopGyroscope = () => {
    if (gyroscopeRef.current) {
      window.removeEventListener('deviceorientation', gyroscopeRef.current);
      gyroscopeRef.current = null;
    }
    setIsGyroscopeActive(false);
    // Mantener la rotaci√≥n actual, no resetear
  };

  const toggleGyroscope = () => {
    if (isGyroscopeActive) {
      stopGyroscope();
    } else {
      startGyroscope();
    }
  };

  return (
    <div className="gyroscope-test-container">
      <div className="gyroscope-header">
        <button className="back-btn" onClick={() => navigate('/hub')}>
          ‚Üê Volver al Hub
        </button>
        <h1>Tour Virtual - Brands Shop</h1>
        <div className="gyroscope-controls">
          {isGyroscopeSupported ? (
            <button 
              className={`gyroscope-btn ${isGyroscopeActive ? 'active' : ''}`}
              onClick={toggleGyroscope}
            >
              {isGyroscopeActive ? 'üîÑ Detener Giroscopio' : 'üì± Activar Giroscopio'}
            </button>
          ) : (
            <span className="not-supported">‚ùå Giroscopio no soportado</span>
          )}
        </div>
      </div>

      {error && (
        <div className="error-message">
          <span>‚ö†Ô∏è</span> {error}
        </div>
      )}

      <div className="canvas-container">
        <canvas ref={canvasRef} className="gyroscope-canvas" />
      </div>

      <div className="status-info">
        <div className="status-item">
          <span className="status-label">Giroscopio:</span>
          <span className={`status-value ${isGyroscopeSupported ? 'supported' : 'not-supported'}`}>
            {isGyroscopeSupported ? '‚úÖ Soportado' : '‚ùå No soportado'}
          </span>
        </div>
        <div className="status-item">
          <span className="status-label">Estado:</span>
          <span className={`status-value ${isGyroscopeActive ? 'active' : 'inactive'}`}>
            {isGyroscopeActive ? 'üîÑ Activo' : '‚è∏Ô∏è Inactivo'}
          </span>
        </div>
        {isGyroscopeActive && (
          <div className="status-item">
            <span className="status-label">Modo:</span>
            <span className="status-value active">üì± Mover dispositivo</span>
          </div>
        )}
        {!isGyroscopeActive && (
          <div className="status-item">
            <span className="status-label">Modo:</span>
            <span className="status-value inactive">üñ±Ô∏è Arrastrar pantalla</span>
          </div>
        )}
      </div>
    </div>
  );
};

export default GyroscopeTest;
