function CarouselSection({ title, cards }) {
  const scrollRef = useRef();

  const scrollByCard = (dir) => {
    if (scrollRef.current) {
      const cardWidth = scrollRef.current.querySelector('.carousel-card')?.offsetWidth || 320;
      scrollRef.current.scrollBy({ left: dir * (cardWidth + 24), behavior: 'smooth' });
    }
  };

  return (
    <section className="carousel-section">
      <div className="carousel-header">
        <h2>{title}</h2>
      </div>
      <div className="carousel-outer">
        <button className="carousel-arrow carousel-arrow-left" onClick={() => scrollByCard(-1)}>&#8592;</button>
        <div className="carousel-cards" ref={scrollRef} style={{ justifyContent: 'center' }}>
          {cards.map((card, i) => (
            <div className="carousel-card" key={i}>
              <img src={card.img} alt={card.name} />
              <div style={{ padding: '1.1rem' }}>
                <h3>{card.name}</h3>
                <div>{card.ciudad}, {card.estado}, {card.pais}</div>
                <button className="carousel-tour-btn" onClick={() => window.location.href = card.tour}>Ir al tour</button>
              </div>
            </div>
          ))}
        </div>
        <button className="carousel-arrow carousel-arrow-right" onClick={() => scrollByCard(1)}>&#8594;</button>
      </div>
    </section>
  );
}

