import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import SceneManager from './SceneManager';
import HotspotEditor from './HotspotEditor';
import api from '../../services/api';
import DragDrop from './DragDrop';
import './TourEditor.css';

function TourEditor({ tourId }) {
  const containerRef = useRef();
  const [tour, setTour] = useState(null);
  const [currentSceneIndex, setCurrentSceneIndex] = useState(0);
  const [selectedHotspot, setSelectedHotspot] = useState(null);
  const [isAddingHotspot, setIsAddingHotspot] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Referencias de Three.js
  const sceneRef = useRef();
  const cameraRef = useRef();
  const rendererRef = useRef();
  const controlsRef = useRef();

  // Cargar tour desde la API
  useEffect(() => {
    const fetchTour = async () => {
      try {
        const response = await api.getTour(tourId);
        setTour(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error al cargar el tour');
        setLoading(false);
        console.error(err);
      }
    };
    fetchTour();
  }, [tourId]);

  // Inicializar Three.js
  useEffect(() => {
    if (!tour || loading) return;

    // Limpiar canvas anterior si existe
    if (rendererRef.current && rendererRef.current.domElement && containerRef.current) {
      if (containerRef.current.contains(rendererRef.current.domElement)) {
        containerRef.current.removeChild(rendererRef.current.domElement);
      }
      rendererRef.current.dispose();
    }

    // Usar el tamaño del contenedor para el renderer
    const width = containerRef.current.clientWidth || window.innerWidth;
    const height = containerRef.current.clientHeight || window.innerHeight;

    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      width / height,
      0.1,
      1000
    );
    camera.position.set(0, 0, 0.1);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setClearColor(0x22232a, 1); // Fondo oscuro, pero no negro puro
    containerRef.current.appendChild(renderer.domElement);

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.rotateSpeed = 0.5;

    sceneRef.current = scene;
    cameraRef.current = camera;
    rendererRef.current = renderer;
    controlsRef.current = controls;

    let animationId;
    const animate = () => {
      animationId = requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };
    animate();

    // Responsivo al tamaño del contenedor
    const handleResize = () => {
      if (!containerRef.current) return;
      const w = containerRef.current.clientWidth || window.innerWidth;
      const h = containerRef.current.clientHeight || window.innerHeight;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    };
    window.addEventListener('resize', handleResize);

    // Limpieza
    return () => {
      window.removeEventListener('resize', handleResize);
      if (animationId) cancelAnimationFrame(animationId);
      if (
        rendererRef.current &&
        rendererRef.current.domElement &&
        containerRef.current &&
        containerRef.current.contains(rendererRef.current.domElement)
      ) {
        rendererRef.current.dispose();
        containerRef.current.removeChild(rendererRef.current.domElement);
      }
    };
  }, [tour, loading]);

  // Cargar la escena actual
  useEffect(() => {
    if (
      !tour ||
      !sceneRef.current ||
      !tour.scenes ||
      !Array.isArray(tour.scenes) ||
      tour.scenes.length === 0 ||
      currentSceneIndex === -1 ||
      !tour.scenes[currentSceneIndex]
    ) return;

    const scene = sceneRef.current;
    const currentScene = tour.scenes[currentSceneIndex];

    // Limpiar escena anterior
    while (scene.children.length > 0) {
      scene.remove(scene.children[0]);
    }

    // Crear esfera para la imagen 360
    const geometry = new THREE.SphereGeometry(500, 60, 40);
    geometry.scale(-1, 1, 1);

    const textureLoader = new THREE.TextureLoader();
    // Corrige la ruta si es relativa
    let imageUrl = currentScene.image;
    if (imageUrl && imageUrl.startsWith('/uploads/')) {
      imageUrl = `${process.env.REACT_APP_API_URL?.replace('/api', '') || 'http://localhost:5000'}${imageUrl}`;
    }

    textureLoader.load(
      imageUrl,
      texture => {
        const material = new THREE.MeshBasicMaterial({
          map: texture,
          side: THREE.DoubleSide
        });
        const sphere = new THREE.Mesh(geometry, material);
        scene.add(sphere);
      },
      undefined,
      err => {
        console.error('Error al cargar la textura:', err);
        const material = new THREE.MeshBasicMaterial({ color: 0x444444, side: THREE.DoubleSide });
        const sphere = new THREE.Mesh(geometry, material);
        scene.add(sphere);
      }
    );
  }, [tour, currentSceneIndex]);

  // Manejar subida de imágenes
  const handleImageUpload = async (e) => {
    let file;
    if (e.target && e.target.files && e.target.files[0]) {
      file = e.target.files[0];
    } else if (e instanceof File) {
      file = e;
    } else if (e && e.target && e.target.files) {
      file = e.target.files[0];
    }
    if (!file) return;

    try {
      const { imageUrl } = await api.uploadImage(file);
      const newScene = {
        name: `Escena ${tour.scenes.length + 1}`,
        image: imageUrl,
        hotspots: []
      };
      const updatedTour = {
        ...tour,
        scenes: [...tour.scenes, newScene]
      };
      const savedTour = await api.updateTour(tourId, updatedTour);
      setTour(savedTour.data ? savedTour.data : savedTour); // Maneja ambos formatos de respuesta
      setCurrentSceneIndex(updatedTour.scenes.length - 1);
    } catch (err) {
      console.error('Error subiendo imagen:', err);
      alert(`Error al subir imagen: ${err.error || 'Intente nuevamente'}`);
    }
  };

  // Manejar subida de imágenes (DragDrop)
  const handleDragDropImage = async (file) => {
    await handleImageUpload(file);
  };

  // Guardar hotspot
  const handleSaveHotspot = async (hotspotData) => {
    try {
      const updatedScenes = [...tour.scenes];
      const currentScene = updatedScenes[currentSceneIndex];

      if (hotspotData._id) {
        const index = currentScene.hotspots.findIndex(h => h._id === hotspotData._id);
        if (index !== -1) {
          currentScene.hotspots[index] = hotspotData;
        }
      } else {
        currentScene.hotspots.push({
          ...hotspotData,
          _id: Date.now().toString()
        });
      }

      const updatedTour = { ...tour, scenes: updatedScenes };
      setTour(updatedTour);
      await api.updateTour(tourId, updatedTour);
      setSelectedHotspot(null);
      setIsAddingHotspot(false);
    } catch (err) {
      console.error('Error guardando hotspot:', err);
      alert(`Error al guardar: ${err.error || 'Intente nuevamente'}`);
    }
  };

  if (loading) {
    return <div className="loading">Cargando tour...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  // Si no hay escenas, muestra mensaje amigable
  if (!tour.scenes || tour.scenes.length === 0) {
    return (
      <div className="tour-editor">
        <div ref={containerRef} className="three-container" style={{ width: '100%', height: '100vh', background: '#22232a' }} />
        <div className="editor-controls" style={{ position: 'absolute', top: 40, left: 40, zIndex: 2 }}>
          <p style={{ color: '#fff', marginBottom: 16 }}>No hay escenas en este tour. Sube una imagen 360° para comenzar.</p>
          <input
            type="file"
            id="image-upload"
            accept="image/*"
            onChange={handleImageUpload}
            style={{ display: 'none' }}
          />
          <button
            className="btn-upload"
            onClick={() => document.getElementById('image-upload').click()}
          >
            Subir Nueva Escena
          </button>
          <DragDrop onFileUpload={handleDragDropImage} />
        </div>
      </div>
    );
  }

  return (
    <div className="tour-editor">
      <div
        ref={containerRef}
        className="three-container"
        style={{ width: '100%', height: '100vh', position: 'absolute', top: 0, left: 0, zIndex: 1 }}
      />
      <div style={{ position: 'relative', zIndex: 2 }}>
        <SceneManager
          scenes={tour.scenes}
          currentIndex={currentSceneIndex}
          onSelectScene={setCurrentSceneIndex}
        />

        <div className="editor-controls">
          <input
            type="file"
            id="image-upload"
            accept="image/*"
            onChange={handleImageUpload}
            style={{ display: 'none' }}
          />
          <button
            className="btn-upload"
            onClick={() => document.getElementById('image-upload').click()}
          >
            Subir Nueva Escena
          </button>
          <DragDrop onFileUpload={handleDragDropImage} />
          <button
            className="btn-add-hotspot"
            onClick={() => setIsAddingHotspot(true)}
          >
            Añadir Hotspot
          </button>
        </div>
      </div>

      {(selectedHotspot || isAddingHotspot) && (
        <HotspotEditor
          hotspot={selectedHotspot || {}}
          scenes={tour.scenes}
          onSave={handleSaveHotspot}
          onCancel={() => {
            setSelectedHotspot(null);
            setIsAddingHotspot(false);
          }}
        />
      )}
    </div>
  );
}

export default TourEditor;
