import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import './GyroscopeTest.css';

function GyroscopeTest() {
  const containerRef = useRef();
  const cameraRef = useRef();
  const sceneRef = useRef();
  const rendererRef = useRef();
  const controlsRef = useRef();
  const [isMobile, setIsMobile] = useState(false);
  const [gyroscopeSupported, setGyroscopeSupported] = useState(false);
  const [deviceOrientation, setDeviceOrientation] = useState({ alpha: 0, beta: 0, gamma: 0 });

  useEffect(() => {
    // Detectar si es dispositivo móvil
    const checkMobile = () => {
      const userAgent = navigator.userAgent || navigator.vendor || window.opera;
      const mobileRegex = /android|webos|iphone|ipad|ipod|blackberry|iemobile|opera mini/i;
      const isMobileDevice = mobileRegex.test(userAgent);
      setIsMobile(isMobileDevice);
      return isMobileDevice;
    };

    const mobile = checkMobile();

    // Verificar soporte de giroscopio
    if (mobile && window.DeviceOrientationEvent) {
      setGyroscopeSupported(true);
    }

    // Configuración de Three.js
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 0.1);
    cameraRef.current = camera;
    sceneRef.current = scene;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    rendererRef.current = renderer;
    containerRef.current.appendChild(renderer.domElement);

    // Controles de órbita para desktop
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.enableZoom = true;
    controls.minDistance = 0.05;
    controls.maxDistance = 2.5;
    controlsRef.current = controls;

    // Cargar imagen 360
    const geometry = new THREE.SphereGeometry(500, 128, 96);
    geometry.scale(-1, 1, 1);

    const textureLoader = new THREE.TextureLoader();
    textureLoader.load('/brandshop1.jpg', texture => {
      texture.minFilter = THREE.LinearFilter;
      texture.magFilter = THREE.LinearFilter;
      if (texture.colorSpace !== undefined && THREE.LinearSRGBColorSpace) {
        texture.colorSpace = THREE.LinearSRGBColorSpace;
      }
      
      const material = new THREE.MeshBasicMaterial({
        map: texture,
        side: THREE.DoubleSide
      });
      
      const sphere = new THREE.Mesh(geometry, material);
      scene.add(sphere);
    });

    // Función de animación
    const animate = () => {
      requestAnimationFrame(animate);
      
      if (mobile && gyroscopeSupported) {
        // Usar datos del giroscopio para rotar la cámara
        const { alpha, beta, gamma } = deviceOrientation;
        
        // Convertir grados a radianes
        const alphaRad = THREE.MathUtils.degToRad(alpha || 0);
        const betaRad = THREE.MathUtils.degToRad(beta || 0);
        const gammaRad = THREE.MathUtils.degToRad(gamma || 0);
        
        // Aplicar rotación basada en el giroscopio
        camera.rotation.set(
          betaRad - Math.PI / 2, // Pitch (rotación X)
          alphaRad, // Yaw (rotación Y)
          gammaRad // Roll (rotación Z)
        );
      } else {
        // Usar controles de órbita para desktop
        controls.update();
      }
      
      renderer.render(scene, camera);
    };
    animate();

    // Manejar eventos de orientación del dispositivo
    const handleDeviceOrientation = (event) => {
      if (mobile && gyroscopeSupported) {
        setDeviceOrientation({
          alpha: event.alpha || 0,
          beta: event.beta || 0,
          gamma: event.gamma || 0
        });
      }
    };

    // Solicitar permisos de orientación del dispositivo
    const requestDeviceOrientationPermission = async () => {
      if (mobile && 'DeviceOrientationEvent' in window) {
        try {
          // Para iOS 13+
          if (typeof DeviceOrientationEvent.requestPermission === 'function') {
            const permission = await DeviceOrientationEvent.requestPermission();
            if (permission === 'granted') {
              window.addEventListener('deviceorientation', handleDeviceOrientation);
            }
          } else {
            // Para Android y otros dispositivos
            window.addEventListener('deviceorientation', handleDeviceOrientation);
          }
        } catch (error) {
          console.error('Error al solicitar permisos de orientación:', error);
        }
      }
    };

    // Manejar cambio de tamaño de ventana
    const handleResize = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;
      
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height);
    };

    window.addEventListener('resize', handleResize);

    // Solicitar permisos al cargar
    requestDeviceOrientationPermission();

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('deviceorientation', handleDeviceOrientation);
      if (containerRef.current && renderer.domElement) {
        containerRef.current.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, [isMobile, gyroscopeSupported, deviceOrientation]);

  return (
    <div className="gyroscope-test-container">
      <div className="gyroscope-header">
        <h2>Prueba de Giroscopio</h2>
        <div className="device-info">
          <p>Dispositivo: {isMobile ? 'Móvil' : 'Desktop'}</p>
          <p>Giroscopio: {gyroscopeSupported ? 'Soportado' : 'No soportado'}</p>
          {isMobile && gyroscopeSupported && (
            <div className="orientation-data">
              <p>Alpha: {deviceOrientation.alpha?.toFixed(2)}°</p>
              <p>Beta: {deviceOrientation.beta?.toFixed(2)}°</p>
              <p>Gamma: {deviceOrientation.gamma?.toFixed(2)}°</p>
            </div>
          )}
        </div>
      </div>
      
      <div 
        ref={containerRef} 
        className="gyroscope-viewer"
        style={{ 
          width: '100%', 
          height: 'calc(100vh - 200px)',
          position: 'relative',
          overflow: 'hidden'
        }}
      />
      
      <div className="gyroscope-instructions">
        {isMobile ? (
          <div>
            <h3>Instrucciones para móvil:</h3>
            <p>• Mueve tu dispositivo para explorar la imagen 360°</p>
            <p>• El movimiento del teléfono se refleja en tiempo real</p>
            <p>• Asegúrate de permitir el acceso al giroscopio cuando se solicite</p>
          </div>
        ) : (
          <div>
            <h3>Instrucciones para desktop:</h3>
            <p>• Usa el mouse para arrastrar y explorar la imagen 360°</p>
            <p>• Usa la rueda del mouse para hacer zoom</p>
            <p>• Prueba esta función en un dispositivo móvil para el giroscopio</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default GyroscopeTest;
