function CarouselSection({ title, cards }) {
  const [index, setIndex] = useState(0);
  const max = cards.length - 1;
  const scrollRef = useRef();

  const scrollTo = (i) => {
    setIndex(i);
    if (scrollRef.current) {
      scrollRef.current.scrollTo({ left: i * 320, behavior: 'smooth' });
    }
  };

  const prev = () => scrollTo(Math.max(index - 1, 0));
  const next = () => scrollTo(Math.min(index + 1, max));

  return (
    <section className="carousel-section">
      <div className="carousel-header">
        <h2>{title}</h2>
        <div className="carousel-controls">
          <button className="carousel-arrow" onClick={prev} disabled={index === 0}>&#8592;</button>
          <button className="carousel-arrow" onClick={next} disabled={index === max}>&#8594;</button>
        </div>
      </div>
      <div className="carousel-cards" ref={scrollRef}>
        {cards.map((card, i) => (
          <div className="carousel-card" key={i} style={{ minWidth: 300, maxWidth: 320, margin: '0 12px', background: '#181c23', borderRadius: 14, boxShadow: '0 2px 12px rgba(56,189,248,0.07)', display: 'inline-block', verticalAlign: 'top', transition: 'transform 0.3s', transform: i === index ? 'scale(1.04)' : 'scale(1)' }}>
            <img src={card.img} alt={card.name} style={{ width: '100%', height: 160, objectFit: 'cover', borderRadius: '14px 14px 0 0' }} />
            <div style={{ padding: '1.1rem' }}>
              <h3 style={{ color: '#38bdf8', fontSize: '1.1rem', margin: '0 0 0.5rem 0' }}>{card.name}</h3>
              <div style={{ color: '#b3b3b3', fontSize: '0.98rem', marginBottom: 8 }}>{card.ciudad}, {card.estado}, {card.pais}</div>
              <button className="carousel-tour-btn" onClick={() => window.location.href = card.tour}>Ir al tour</button>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

