import React, { useState } from 'react';
import './HotspotCreationModal.css';

function HotspotCreationModal({ position, onSave, onCancel, scenes = [] }) {
  const [hotspotType, setHotspotType] = useState('access');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [selectedSceneId, setSelectedSceneId] = useState('');
  const [socialMedia, setSocialMedia] = useState({
    facebook: '',
    instagram: '',
    twitter: ''
  });

  // Selección de imagen para "access"
  const handleSelectScene = (sceneId) => {
    setSelectedSceneId(sceneId);
  };

  const handleSocialMediaChange = (e) => {
    const { name, value } = e.target;
    setSocialMedia(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    const hotspotData = {
      type: hotspotType,
      pitch: position.pitch,
      yaw: position.yaw,
      title,
      description
    };

    if (hotspotType === 'access') {
      hotspotData.targetSceneId = selectedSceneId;
    } else if (hotspotType === 'commerce') {
      hotspotData.socialMedia = socialMedia;
    }

    onSave(hotspotData);
  };

  return (
    <div className="modal-overlay">
      <div className="hotspot-creation-modal">
        <div className="modal-header">
          <h3>Crear Nuevo Hotspot</h3>
          <button className="close-btn" onClick={onCancel}>×</button>
        </div>
        <div className="modal-body">
          <div className="form-group">
            <label>Tipo de Hotspot</label>
            <select 
              value={hotspotType} 
              onChange={e => {
                setHotspotType(e.target.value);
                setSelectedSceneId('');
              }}
            >
              <option value="access">Punto de Acceso</option>
              <option value="commerce">Comercio</option>
              <option value="location">Locación</option>
            </select>
          </div>
          <div className="form-group">
            <label>Título *</label>
            <input 
              type="text" 
              value={title} 
              onChange={e => setTitle(e.target.value)} 
              placeholder="Nombre del hotspot"
              required
            />
          </div>
          <div className="form-group">
            <label>Descripción</label>
            <textarea 
              value={description} 
              onChange={e => setDescription(e.target.value)} 
              placeholder="Descripción detallada"
              rows="3"
            />
          </div>
          {hotspotType === 'access' && (
            <div className="form-group">
              <label>Selecciona la imagen destino *</label>
              <div className="image-select-list">
                {scenes && scenes.length > 0 ? (
                  scenes.map(scene => (
                    <div
                      key={scene._id}
                      className={`image-select-thumb${selectedSceneId === scene._id ? ' selected' : ''}`}
                      onClick={() => handleSelectScene(scene._id)}
                      title={scene.name}
                    >
                      {scene.image ? (
                        <img src={scene.image} alt={scene.name} />
                      ) : (
                        <span>Sin imagen</span>
                      )}
                    </div>
                  ))
                ) : (
                  <span style={{ color: '#a3a3a3' }}>No hay imágenes disponibles</span>
                )}
              </div>
            </div>
          )}
          {hotspotType === 'commerce' && (
            <div className="social-media-fields">
              <div className="form-group">
                <label>Facebook</label>
                <input 
                  type="url" 
                  name="facebook" 
                  value={socialMedia.facebook} 
                  onChange={handleSocialMediaChange} 
                  placeholder="https://facebook.com/perfil"
                />
              </div>
              <div className="form-group">
                <label>Instagram</label>
                <input 
                  type="url" 
                  name="instagram" 
                  value={socialMedia.instagram} 
                  onChange={handleSocialMediaChange} 
                  placeholder="https://instagram.com/perfil"
                />
              </div>
              <div className="form-group">
                <label>Twitter</label>
                <input 
                  type="url" 
                  name="twitter" 
                  value={socialMedia.twitter} 
                  onChange={handleSocialMediaChange} 
                  placeholder="https://twitter.com/perfil"
                />
              </div>
            </div>
          )}
        </div>
        <div className="modal-footer">
          <button className="btn-cancel" onClick={onCancel}>Cancelar</button>
          <button 
            className="btn-save"
            onClick={handleSubmit}
            disabled={
              !title ||
              (hotspotType === 'access' && !selectedSceneId)
            }
          >
            Guardar Hotspot
          </button>
        </div>
      </div>
    </div>
  );
}

export default HotspotCreationModal;
