import React, { useEffect, useState, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import { useNavigate } from 'react-router-dom';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import api from '../services/api';
import './LandingMap.css';

// Fix default icon issue with Leaflet in React
import iconUrl from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
let DefaultIcon = L.icon({
  iconUrl,
  shadowUrl: iconShadow,
  iconAnchor: [12, 41],
});
L.Marker.prototype.options.icon = DefaultIcon;

const ciudadGuayana = [8.3667, -62.6500];

function LandingMap() {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTour, setSelectedTour] = useState(null);
  const [panelOpen, setPanelOpen] = useState(false);
  const navigate = useNavigate();
  const intervalRef = useRef();

  // Polling para refrescar los tours cada 2 segundos
  useEffect(() => {
    let isMounted = true;
    const fetchTours = async () => {
      try {
        const response = await api.getTours();
        if (isMounted) {
          setTours(response.data);
          setLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError('Error al cargar los tours');
          setLoading(false);
        }
      }
    };
    fetchTours();
    intervalRef.current = setInterval(fetchTours, 2000);
    return () => {
      isMounted = false;
      clearInterval(intervalRef.current);
    };
  }, []);

  const handleMarkerClick = (tour) => {
    setSelectedTour(tour);
    setPanelOpen(true);
  };

  const handleClosePanel = () => {
    setPanelOpen(false);
    setSelectedTour(null);
  };

  const handleViewTour = () => {
    if (selectedTour) {
      navigate(`/viewer/${selectedTour._id}`);
    }
  };

  const handleEditTour = () => {
    if (selectedTour) {
      navigate(`/editor/${selectedTour._id}`);
    }
  };

  if (loading) {
    return <div style={{ color: '#38bdf8', textAlign: 'center', marginTop: 40 }}>Cargando mapa...</div>;
  }
  if (error) {
    return <div style={{ color: 'red', textAlign: 'center', marginTop: 40 }}>{error}</div>;
  }

  // Solo mostrar tours con localización válida
  const validTours = tours.filter(t => t.localizacion && typeof t.localizacion.lat === 'number' && typeof t.localizacion.lng === 'number');

  // El mapa siempre centrado en Ciudad Guayana
  return (
    <div style={{ width: '100vw', height: '100vh', position: 'relative' }}>
      <button
        style={{
          position: 'absolute',
          top: 24,
          left: 24,
          zIndex: 1000,
          background: '#23272f',
          color: '#38bdf8',
          border: 'none',
          borderRadius: 6,
          padding: '10px 20px',
          fontSize: '1rem',
          fontWeight: 500,
          cursor: 'pointer',
          transition: 'background 0.2s, color 0.2s'
        }}
        onClick={() => navigate('/hub')}
      >
        ← Volver al Hub
      </button>
      
      <MapContainer center={ciudadGuayana} zoom={12} style={{ width: '100%', height: '100%' }}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {validTours.map(tour => (
          <Marker
            key={tour._id}
            position={[tour.localizacion.lat, tour.localizacion.lng]}
            eventHandlers={{
              click: () => handleMarkerClick(tour)
            }}
          >
            <Popup>
              <strong>{tour.name}</strong><br />
              {tour.description}<br />
              <span style={{ color: '#38bdf8', cursor: 'pointer' }} onClick={() => navigate(`/viewer/${tour._id}`)}>
                Ver tour 360
              </span>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {/* Panel de detalles del tour */}
      <aside className={`map-panel${panelOpen ? ' open' : ''}`}>
        <div className="panel-header">
          <span className="panel-title">Detalles del Tour</span>
          <button className="panel-toggle" onClick={handleClosePanel}>
            &#10094;
          </button>
        </div>
        {panelOpen && selectedTour && (
          <div className="panel-content">
            <div className="tour-details">
              <div className="tour-thumbnail">
                {selectedTour.scenes?.[0]?.image ? (
                  <img 
                    src={selectedTour.scenes[0].image.startsWith('/uploads/') 
                      ? `${process.env.REACT_APP_API_URL?.replace('/api', '') || 'http://localhost:5000'}${selectedTour.scenes[0].image}`
                      : selectedTour.scenes[0].image
                    }
                    alt={selectedTour.name}
                    onError={(e) => e.target.src = 'default-tour.jpg'}
                  />
                ) : (
                  <div className="thumbnail-placeholder">🖼️</div>
                )}
              </div>
              
              <div className="tour-info">
                <h3>{selectedTour.name}</h3>
                <p className="tour-description">{selectedTour.description || 'Sin descripción'}</p>
                
                <div className="tour-meta">
                  <span className="tour-scenes">
                    <strong>{selectedTour.scenes?.length || 0}</strong> escenas
                  </span>
                  <span className="tour-hotspots">
                    <strong>{selectedTour.scenes?.reduce((total, scene) => total + (scene.hotspots?.length || 0), 0) || 0}</strong> hotspots
                  </span>
                </div>
                
                <div className="tour-footer">
                  <span className="tour-date">
                    {new Date(selectedTour.createdAt).toLocaleDateString()}
                  </span>
                  <span className="tour-api-key">
                    {selectedTour.apiKey ? `${selectedTour.apiKey.substring(0, 8)}...` : ''}
                  </span>
                </div>
              </div>
            </div>
            
            <div className="tour-actions">
              <button className="btn-view-tour" onClick={handleViewTour}>
                👁️ Ver Tour 360°
              </button>
              <button className="btn-edit-tour" onClick={handleEditTour}>
                ✏️ Editar Tour
              </button>
            </div>
          </div>
        )}
      </aside>
      
      {!panelOpen && (
        <button
          className="panel-toggle-collapsed"
          onClick={() => setPanelOpen(true)}
          aria-label="Abrir panel de detalles"
        >
          &#10095;
        </button>
      )}
    </div>
  );
}

export default LandingMap;
