  ctx.shadowColor = '#0ea5e9';
  ctx.shadowBlur = 8;
  ctx.fill();
  ctx.lineWidth = 4;
  ctx.strokeStyle = '#fff';
  ctx.stroke();
  if (hotspot.type === 'access') {
    ctx.beginPath();
