import React, { useEffect, useState } from 'react';
import { useCart } from '../../context/CartContext';
import './Shop.css';

// Utilidad para obtener la URL absoluta de la imagen
function getAbsoluteImageUrl(image) {
  if (!image) return '';
  if (image.startsWith('/uploads/')) {
    return `${process.env.REACT_APP_API_URL?.replace('/api', '') || 'http://localhost:5000'}${image}`;
  }
  return image;
}

function Shop({ tourId }) {
  const [products, setProducts] = useState([]);
  const [tour, setTour] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('todos');
  const { addToCart } = useCart();

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        // Obtener datos del tour (nombre de la tienda)
        const tourRes = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/tours/${tourId}`);
        const tourData = await tourRes.json();
        setTour(tourData);

        // Obtener productos asociados a este tour/tienda
        const prodRes = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/products/by-tour/${tourId}`);
        if (!prodRes.ok) {
          setProducts([]);
        } else {
          const prodData = await prodRes.json();
          setProducts(Array.isArray(prodData) ? prodData : []);
        }
      } catch (err) {
        setProducts([]);
      }
      setLoading(false);
    }
    if (tourId) fetchData();
  }, [tourId]);

  const handleAddToCart = (product) => {
    addToCart(product, tourId, 1);
    // Mostrar confirmación visual
    showAddToCartFeedback(product.name);
  };

  const showAddToCartFeedback = (productName) => {
    // Crear elemento de feedback visual
    const feedback = document.createElement('div');
    feedback.className = 'add-to-cart-feedback';
    feedback.textContent = `¡${productName} agregado!`;
    document.body.appendChild(feedback);

    // Remover después de la animación
    setTimeout(() => {
      feedback.remove();
    }, 2000);
  };

  const filteredProducts = selectedCategory === 'todos' 
    ? products 
    : products.filter(product => product.category === selectedCategory);

  if (loading) return (
    <div className="shop-loading">
      <div className="loading-spinner"></div>
      <p>Cargando tienda...</p>
    </div>
  );

  return (
    <div className="shop-container">
      {/* Header móvil optimizado */}
      <header className="shop-header">
        <div className="shop-header-top">
          <button
            className="shop-back-btn"
            onClick={() => window.history.back()}
            aria-label="Volver atrás"
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M19 12H5M12 19l-7-7 7-7"/>
            </svg>
          </button>
          <div className="shop-header-info">
            <h1 className="shop-title">{tour?.name || 'Tienda'}</h1>
            <p className="shop-subtitle">Descubre nuestros productos</p>
          </div>
        </div>
      </header>

      {/* Banner principal con imagen de fondo */}
      <div className="shop-hero">
        <div className="shop-hero-overlay"></div>
        <div className="shop-hero-content">
          <div className="shop-hero-text">
            <h2>{tour?.name || 'Tienda'}</h2>
            <p>{tour?.description || 'Explora nuestra selección de productos de calidad'}</p>
          </div>
          <div className="shop-hero-stats">
            <div className="stat-item">
              <span className="stat-number">{products.length}</span>
              <span className="stat-label">Productos</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">24/7</span>
              <span className="stat-label">Disponible</span>
            </div>
          </div>
        </div>
      </div>

      {/* Contenido principal */}
      <main className="shop-main">
        {products.length === 0 ? (
          <div className="shop-empty-state">
            <div className="empty-state-icon">🛍️</div>
            <h3>No hay productos disponibles</h3>
            <p>Esta tienda aún no tiene productos. Vuelve más tarde.</p>
          </div>
        ) : (
          <>
            {/* Filtros y búsqueda */}
            <div className="shop-controls">
              <div className="shop-search">
                <div className="search-input-wrapper">
                  <svg className="search-icon" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <circle cx="11" cy="11" r="8"/>
                    <path d="m21 21-4.35-4.35"/>
                  </svg>
                  <input 
                    type="text" 
                    placeholder="Buscar productos..." 
                    className="search-input"
                  />
                </div>
              </div>
              
              <div className="shop-filters">
                <button 
                  className={`filter-btn ${selectedCategory === 'todos' ? 'active' : ''}`}
                  onClick={() => setSelectedCategory('todos')}
                >
                  Todos
                </button>
                <button 
                  className={`filter-btn ${selectedCategory === 'bebidas' ? 'active' : ''}`}
                  onClick={() => setSelectedCategory('bebidas')}
                >
                  Bebidas
                </button>
                <button 
                  className={`filter-btn ${selectedCategory === 'comida' ? 'active' : ''}`}
                  onClick={() => setSelectedCategory('comida')}
                >
                  Comida
                </button>
              </div>
            </div>

            {/* Información de productos */}
            <div className="shop-products-info">
              <div className="products-count">
                <h3>Productos ({filteredProducts.length})</h3>
                <p>Selecciona los productos que deseas agregar a tu carrito</p>
              </div>
              <div className="products-sort">
                <select className="sort-select">
                  <option value="default">Ordenar por</option>
                  <option value="price-low">Precio: Menor a Mayor</option>
                  <option value="price-high">Precio: Mayor a Menor</option>
                  <option value="name">Nombre A-Z</option>
                </select>
              </div>
            </div>

            {/* Grid de productos optimizado para móvil */}
            <div className="shop-products-grid">
              {filteredProducts.map(product => (
                <div className="shop-product-card" key={product._id}>
                  <div className="product-image-container">
                    <img
                      src={getAbsoluteImageUrl(product.image)}
                      alt={product.name}
                      className="product-image"
                      onError={e => { 
                        e.target.src = 'https://via.placeholder.com/300x300/23272f/38bdf8?text=Sin+Imagen'; 
                      }}
                    />
                    <div className="product-overlay">
                      <button
                        className="product-quick-add"
                        onClick={() => handleAddToCart(product)}
                        aria-label={`Agregar ${product.name} al carrito`}
                      >
                        <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <path d="M12 5v14M5 12h14"/>
                        </svg>
                        Agregar
                      </button>
                    </div>
                    <div className="product-badge">
                      {product.category === 'bebidas' && '🥤'}
                      {product.category === 'comida' && '🍽️'}
                    </div>
                  </div>
                  
                  <div className="product-content">
                    <div className="product-header">
                      <h3 className="product-name">{product.name}</h3>
                      <span className="product-category">{product.category}</span>
                    </div>
                    <p className="product-description">{product.description}</p>
                    <div className="product-footer">
                      <div className="product-price-container">
                        <span className="product-price">${product.price.toFixed(2)}</span>
                        {product.originalPrice && product.originalPrice > product.price && (
                          <span className="product-original-price">${product.originalPrice.toFixed(2)}</span>
                        )}
                      </div>
                      <button
                        className="product-add-btn"
                        onClick={() => handleAddToCart(product)}
                        aria-label={`Agregar ${product.name} al carrito`}
                      >
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <path d="M12 5v14M5 12h14"/>
                        </svg>
                        Agregar
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Paginación móvil */}
            {filteredProducts.length > 6 && (
              <div className="shop-pagination">
                <button className="pagination-btn" disabled>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="m15 18-6-6 6-6"/>
                  </svg>
                </button>
                <span className="pagination-info">1 de 1</span>
                <button className="pagination-btn" disabled>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="m9 18 6-6-6-6"/>
                  </svg>
                </button>
              </div>
            )}
          </>
        )}
      </main>

      {/* Botón flotante de carrito */}
      <button className="floating-cart-btn" onClick={() => window.location.href = '/cart'}>
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <path d="M3 3h2l.4 2M7 13h10l4-8H5.4m0 0L7 13m0 0 2.5 5M7 13l-2.5 5m0 0h9.4M9 20a1 1 0 1 0 0 2 1 1 0 0 0 0-2Zm6 0a1 1 0 1 0 0 2 1 1 0 0 0 0-2Z"/>
        </svg>
        <span className="cart-badge">0</span>
      </button>
    </div>
  );
}

export default Shop;
