import React, { useRef, useEffect, useState, useCallback } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import './EmbedViewer.css';
import RadialFadeMaterial from '../shaders/RadialFadeMaterial';

// Hotspot mejorado con efectos visuales similares a Kuula
function createHotspotSprite(hotspot) {
  const size = 80;
  const canvas = document.createElement('canvas');
  canvas.width = size;
  canvas.height = size;
  const ctx = canvas.getContext('2d');

  // Fondo con gradiente
  const gradient = ctx.createRadialGradient(size/2, size/2, 0, size/2, size/2, size/2);
  gradient.addColorStop(0, 'rgba(56, 189, 248, 0.9)');
  gradient.addColorStop(0.7, 'rgba(14, 165, 233, 0.7)');
  gradient.addColorStop(1, 'rgba(14, 165, 233, 0.3)');

  ctx.beginPath();
  ctx.arc(size/2, size/2, size/2-6, 0, 2*Math.PI);
  ctx.fillStyle = gradient;
  ctx.shadowColor = '#0ea5e9';
  ctx.shadowBlur = 12;
  ctx.fill();

  // Borde exterior
  ctx.lineWidth = 3;
  ctx.strokeStyle = '#ffffff';
  ctx.stroke();

  // Borde interior
  ctx.beginPath();
  ctx.arc(size/2, size/2, size/2-12, 0, 2*Math.PI);
  ctx.lineWidth = 2;
  ctx.strokeStyle = 'rgba(255, 255, 255, 0.6)';
  ctx.stroke();

  // Icono segÃºn el tipo
  if (hotspot.type === 'access') {
    // Flecha de navegaciÃ³n
    ctx.beginPath();
    ctx.moveTo(size/2, size/2-15);
    ctx.lineTo(size/2+12, size/2+5);
    ctx.lineTo(size/2-12, size/2+5);
    ctx.closePath();
    ctx.fillStyle = '#ffffff';
    ctx.fill();

    // Punto central
    ctx.beginPath();
    ctx.arc(size/2, size/2+8, 3, 0, 2*Math.PI);
    ctx.fillStyle = '#ffffff';
    ctx.fill();
  }

  const texture = new THREE.CanvasTexture(canvas);
  const material = new THREE.SpriteMaterial({ 
    map: texture, 
    depthTest: false,
    transparent: true,
    opacity: 0.9
  });
  const sprite = new THREE.Sprite(material);
  sprite.scale.set(25, 25, 1);
  sprite.userData.hotspot = hotspot;
  return sprite;
}

function isMobileDevice() {
  return /Android|iPhone|iPad|iPod|Opera Mini|IEMobile|WPDesktop/i.test(navigator.userAgent);
}

function EmbedViewer({ tour }) {
  const containerRef = useRef();
  const [currentSceneIndex, setCurrentSceneIndex] = useState(0);
  const [fade, setFade] = useState(false);
  const [pendingSceneIndex, setPendingSceneIndex] = useState(null);
  const cameraRef = useRef();
  const [transitioning, setTransitioning] = useState(false);
  const [transitionProgress, setTransitionProgress] = useState(0);
  const [prevTexture, setPrevTexture] = useState(null);
  const [isMobile, setIsMobile] = useState(false);
  const [autoRotate, setAutoRotate] = useState(false);
  const [fullscreen, setFullscreen] = useState(false);
  const [showSceneSelector, setShowSceneSelector] = useState(false);

  const sceneRef = useRef();
  const rendererRef = useRef();
  const controlsRef = useRef();
  const animationRef = useRef();

  useEffect(() => {
    setIsMobile(isMobileDevice());
  }, []);

  // NavegaciÃ³n con teclado
  const handleKeyDown = useCallback((event) => {
    if (!tour || !tour.scenes) return;

    switch(event.key) {
      case 'ArrowLeft':
        event.preventDefault();
        navigateToScene(currentSceneIndex - 1);
        break;
      case 'ArrowRight':
        event.preventDefault();
        navigateToScene(currentSceneIndex + 1);
        break;
      case ' ':
        event.preventDefault();
        setAutoRotate(!autoRotate);
        break;
      case 'f':
        event.preventDefault();
        toggleFullscreen();
        break;
      case 'Escape':
        if (fullscreen) {
          toggleFullscreen();
        }
        break;
    }
  }, [tour, currentSceneIndex, autoRotate, fullscreen]);

  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  // NavegaciÃ³n entre escenas
  const navigateToScene = useCallback((index) => {
    if (!tour || !tour.scenes || index < 0 || index >= tour.scenes.length) return;
    if (transitioning) return;

    startTransition(index);
  }, [tour, transitioning]);

  // Toggle fullscreen
  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      containerRef.current?.requestFullscreen();
      setFullscreen(true);
    } else {
      document.exitFullscreen();
      setFullscreen(false);
    }
  };

  // TransiciÃ³n mejorada
  const startTransition = useCallback((targetIdx) => {
    if (transitioning) return;

    const scene = sceneRef.current;
    const renderer = rendererRef.current;
    const camera = cameraRef.current;

    if (!scene || !renderer || !camera) return;

    // Capturar textura de la escena actual
    const renderTarget = new THREE.WebGLRenderTarget(
      renderer.domElement.width,
      renderer.domElement.height
    );
    renderer.setRenderTarget(renderTarget);
    renderer.render(scene, camera);
    renderer.setRenderTarget(null);

    setPrevTexture(renderTarget.texture);
    setTransitioning(true);
    setTransitionProgress(0);
    setPendingSceneIndex(targetIdx);

    // AnimaciÃ³n de transiciÃ³n
    const startTime = Date.now();
    const duration = 800;

    const animateTransition = () => {
      const elapsed = Date.now() - startTime;
      const progress = Math.min(elapsed / duration, 1);

      // Easing suave
      const easedProgress = 1 - Math.pow(1 - progress, 3);
      setTransitionProgress(easedProgress);

      if (progress < 1) {
        animationRef.current = requestAnimationFrame(animateTransition);
      } else {
        // Finalizar transiciÃ³n
        setCurrentSceneIndex(targetIdx);
        setTransitioning(false);
        setPendingSceneIndex(null);
        setPrevTexture(null);
        if (renderTarget) {
          renderTarget.dispose();
        }
      }
    };

    animationRef.current = requestAnimationFrame(animateTransition);
  }, [transitioning]);

  // Efecto de vibraciÃ³n en mÃ³viles para feedback tÃ¡ctil
  const triggerHapticFeedback = () => {
    if (navigator.vibrate && isMobile) {
      navigator.vibrate(50);
    }
  };

  // NavegaciÃ³n con feedback hÃ¡ptico
  const navigateToSceneWithFeedback = useCallback((index) => {
    triggerHapticFeedback();
    navigateToScene(index);
  }, [navigateToScene]);

  useEffect(() => {
    if (!tour || !tour.scenes || tour.scenes.length === 0) return;

    // ConfiguraciÃ³n de Three.js optimizada para alta calidad
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75, 
      window.innerWidth / window.innerHeight, 
      0.1, 
      1000
    );
    camera.position.set(0, 0, 0.1);
    cameraRef.current = camera;
    sceneRef.current = scene;

    const renderer = new THREE.WebGLRenderer({ 
      antialias: true,
      alpha: true,
      powerPreference: "high-performance",
      preserveDrawingBuffer: true
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    renderer.toneMapping = THREE.ACESFilmicToneMapping;
    renderer.toneMappingExposure = 1.0;
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    rendererRef.current = renderer;
    containerRef.current.appendChild(renderer.domElement);

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.rotateSpeed = -0.5;
    controls.enableZoom = true;
    controls.minDistance = 0.05;
    controls.maxDistance = 2.5;
    controls.autoRotate = autoRotate;
    controls.autoRotateSpeed = 0.5;
    controlsRef.current = controls;

    // Zoom con scroll mejorado
    renderer.domElement.addEventListener('wheel', (e) => {
      e.preventDefault();
      const zoomSpeed = 0.1;
      const zoomDelta = e.deltaY > 0 ? -zoomSpeed : zoomSpeed;
      camera.fov = Math.max(30, Math.min(100, camera.fov + zoomDelta * 10));
      camera.updateProjectionMatrix();
    }, { passive: false });

    // Cargar escena actual
    const loadScene = (index) => {
      // Limpiar escena
      while(scene.children.length > 0) { 
        scene.remove(scene.children[0]); 
      }

      const sceneData = tour.scenes[index];
      const geometry = new THREE.SphereGeometry(500, 128, 96);
      geometry.scale(-1, 1, 1);

      const textureLoader = new THREE.TextureLoader();
      textureLoader.load(sceneData.image, texture => {
        texture.minFilter = THREE.LinearFilter;
        texture.magFilter = THREE.LinearFilter;
        texture.generateMipmaps = false;
        texture.anisotropy = renderer.capabilities.getMaxAnisotropy();

        if (texture.colorSpace !== undefined && THREE.LinearSRGBColorSpace) {
          texture.colorSpace = THREE.LinearSRGBColorSpace;
        }

        const material = new THREE.MeshBasicMaterial({ 
          map: texture,
          side: THREE.DoubleSide
        });
        const sphere = new THREE.Mesh(geometry, material);
        scene.add(sphere);

        // Renderizar hotspots mejorados
        if (Array.isArray(sceneData.hotspots)) {
          sceneData.hotspots.forEach(hotspot => {
            if (hotspot.type === 'access') {
              const radius = 500;
              const phi = THREE.MathUtils.degToRad(90 - hotspot.pitch);
              const theta = THREE.MathUtils.degToRad(hotspot.yaw);
              const x = radius * Math.sin(phi) * Math.sin(theta);
              const y = radius * Math.cos(phi);
              const z = radius * Math.sin(phi) * Math.cos(theta);

              const sprite = createHotspotSprite(hotspot);
              sprite.position.set(x, y, z);
              sprite.userData.hotspot = hotspot;
              sprite.userData.isAccessHotspot = true;
              scene.add(sprite);
            }
          });
        }
      });
    };

    // Cargar primera escena
    loadScene(currentSceneIndex);

    // AnimaciÃ³n mejorada
    let hoveredObj = null;
    const animate = () => {
      requestAnimationFrame(animate);

      // Actualizar controles
      if (controls) {
        controls.autoRotate = autoRotate;
        controls.update();
      }

      // AnimaciÃ³n de hotspots
      const accessSpheres = [];
      scene.traverse(obj => {
        if (obj.userData && obj.userData.isAccessHotspot) {
          accessSpheres.push(obj);
        }
      });

      if (accessSpheres.length > 0) {
        const t = Date.now() * 0.002;
        accessSpheres.forEach((sphere, index) => {
          // AnimaciÃ³n de pulso
          let scale = 1.0 + 0.1 * Math.sin(t * 2 + index * 0.5);
          if (sphere === hoveredObj) {
            scale *= 1.3;
            sphere.material.opacity = 1.0;
          } else {
            sphere.material.opacity = 0.8 + 0.2 * Math.sin(t + index);
          }
          sphere.scale.set(scale, scale, 1);

          // RotaciÃ³n sutil
          sphere.rotation.z = t * 0.5 + index * 0.3;
        });
      }

      renderer.render(scene, camera);
    };
    animate();

    // Doble click para navegar entre escenas por hotspot
    let lastClickTime = 0;
    const dom = renderer.domElement;
    function getAccessSpheres() {
      const arr = [];
      scene.traverse(obj => {
        if (obj.userData && obj.userData.isAccessHotspot) arr.push(obj);
      });
      return arr;
    }
    function onPointerDown(event) {
      if (event.button !== 0) return;
      const rect = dom.getBoundingClientRect();
      const mouse = new THREE.Vector2(
        ((event.clientX - rect.left) / rect.width) * 2 - 1,
        -((event.clientY - rect.top) / rect.height) * 2 + 1
      );
      const raycaster = new THREE.Raycaster();
      const accessSpheres = getAccessSpheres();
      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(accessSpheres, true);
      if (intersects.length > 0) {
        const obj = intersects[0].object;
        const now = Date.now();
        if (obj.userData.isAccessHotspot) {
          if (now - lastClickTime < 400) {
            const hotspot = obj.userData.hotspot;
            if (hotspot && hotspot.type === 'access' && hotspot.targetSceneId) {
              const idx = tour.scenes.findIndex(s => String(s._id) === String(hotspot.targetSceneId));
              if (idx !== -1) {
                navigateToSceneWithFeedback(idx);
              }
            }
          }
          lastClickTime = now;
        }
      }
    }
    function onPointerMove(event) {
      const rect = dom.getBoundingClientRect();
      const mouse = new THREE.Vector2(
        ((event.clientX - rect.left) / rect.width) * 2 - 1,
        -((event.clientY - rect.top) / rect.height) * 2 + 1
      );
      const raycaster = new THREE.Raycaster();
      const accessSpheres = getAccessSpheres();
      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(accessSpheres, true);
      if (intersects.length > 0) {
        hoveredObj = intersects[0].object;
        dom.style.cursor = 'pointer';
      } else {
        hoveredObj = null;
        dom.style.cursor = '';
      }
    }
    dom.addEventListener('pointerdown', onPointerDown);
    dom.addEventListener('pointermove', onPointerMove);

    // Manejar redimensionamiento
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', handleResize);

    // Limpieza
    return () => {
      window.removeEventListener('resize', handleResize);
      if (renderer) {
        renderer.dispose();
        containerRef.current.removeChild(renderer.domElement);
      }
      dom.removeEventListener('pointerdown', onPointerDown);
      dom.removeEventListener('pointermove', onPointerMove);
    };
  }, [tour, currentSceneIndex, autoRotate, fullscreen, isMobile, navigateToSceneWithFeedback]);

  // AnimaciÃ³n de transiciÃ³n
  useEffect(() => {
    if (!transitioning) return;
    let frame;
    function animate() {
      setTransitionProgress(prev => {
        const next = Math.min(prev + 0.04, 1);
        if (next < 1) {
          frame = requestAnimationFrame(animate);
        } else {
          setTransitioning(false);
          setPrevTexture(null);
          setCurrentSceneIndex(pendingSceneIndex);
        }
        return next;
      });
    }
    animate();
    return () => cancelAnimationFrame(frame);
  }, [transitioning]);

  useEffect(() => {
    if (!sceneRef.current || !rendererRef.current || !cameraRef.current) return;
    if (!transitioning || !prevTexture) return;
    const geometry = new THREE.SphereGeometry(500, 128, 96);
    geometry.scale(-1, 1, 1);
    const currentScene = tour.scenes[pendingSceneIndex];
    const loader = new THREE.TextureLoader();
    loader.load(currentScene.image, nextTexture => {
      const material = RadialFadeMaterial(prevTexture, nextTexture, transitionProgress);
      const sphere = new THREE.Mesh(geometry, material);
      sceneRef.current.add(sphere);
      function renderTransition() {
        material.uniforms.uProgress.value = transitionProgress;
        rendererRef.current.render(sceneRef.current, cameraRef.current);
        if (transitioning) requestAnimationFrame(renderTransition);
        else sceneRef.current.remove(sphere);
      }
      renderTransition();
    });
  }, [transitioning, transitionProgress]);

  // Cuando cambia la escena, hacer fade out y zoom out
  useEffect(() => {
    if (pendingSceneIndex === null) return;
    // Animar FOV (zoom out) y quitar fade
    const camera = cameraRef.current;
    if (!camera) return;
    let startFov = camera.fov;
    let endFov = 75;
    let duration = 400;
    let start = null;
    function animateZoomOut(ts) {
      if (!start) start = ts;
      let progress = Math.min((ts - start) / duration, 1);
      camera.fov = startFov + (endFov - startFov) * progress;
      camera.updateProjectionMatrix();
      if (progress < 1) {
        requestAnimationFrame(animateZoomOut);
      } else {
        setFade(false);
        setPendingSceneIndex(null);
      }
    }
    setTimeout(() => {
      requestAnimationFrame(animateZoomOut);
    }, 250);
  }, [currentSceneIndex]);

  // Handler para botones de escena
  function handleSceneBtn(idx) {
    if (idx === currentSceneIndex) return;
    navigateToSceneWithFeedback(idx);
  }

  return (
    <div className="embed-viewer">
      <div ref={containerRef} className="three-container"></div>
      <div className={`fade-overlay${fade ? ' visible' : ''}`}></div>

      {/* Controles principales */}
      <div className="viewer-controls">
        <div className="control-buttons">
          <button 
            className={`control-btn ${autoRotate ? 'active' : ''}`}
            onClick={() => setAutoRotate(!autoRotate)}
            title="RotaciÃ³n automÃ¡tica (Espacio)"
          >
            ðŸ”„
          </button>

          <button 
            className="control-btn"
            onClick={toggleFullscreen}
            title="Pantalla completa (F)"
          >
            {fullscreen ? 'â›¶' : 'â›¶'}
          </button>

          <button 
            className={`control-btn ${showSceneSelector ? 'active' : ''}`}
            onClick={() => setShowSceneSelector(!showSceneSelector)}
            title="Selector de escenas"
          >
            ðŸ“‹
          </button>
        </div>
      </div>

      {/* Selector de escenas */}
      {showSceneSelector && tour.scenes.length > 1 && (
        <div className="scene-selector">
          {tour.scenes.map((scene, index) => (
            <button
              key={index}
              className={`scene-btn ${index === currentSceneIndex ? 'active' : ''}`}
              onClick={() => navigateToSceneWithFeedback(index)}
              disabled={transitioning}
            >
              {index + 1}
            </button>
          ))}
        </div>
      )}

      {/* NavegaciÃ³n con flechas */}
      {tour.scenes.length > 1 && (
        <div className="navigation-arrows">
          <button 
            className="nav-arrow left"
            onClick={() => navigateToSceneWithFeedback(currentSceneIndex - 1)}
            disabled={currentSceneIndex === 0 || transitioning}
          >
            â€¹
          </button>
          <button 
            className="nav-arrow right"
            onClick={() => navigateToSceneWithFeedback(currentSceneIndex + 1)}
            disabled={currentSceneIndex === tour.scenes.length - 1 || transitioning}
          >
            â€º
          </button>
        </div>
      )}

      {/* InformaciÃ³n de la escena actual */}
      <div className="scene-info">
        <div className="scene-title">
          {tour.scenes[currentSceneIndex]?.title || `Escena ${currentSceneIndex + 1}`}
        </div>
        <div className="scene-description">
          {tour.scenes[currentSceneIndex]?.description || 'Tour virtual 360Â°'}
        </div>
      </div>

      {/* Indicador de progreso */}
      {tour.scenes.length > 1 && (
        <div className="progress-indicator">
          <div className="progress-bar">
            <div 
              className="progress-fill"
              style={{ width: `${((currentSceneIndex + 1) / tour.scenes.length) * 100}%` }}
            ></div>
          </div>
          <span className="progress-text">
            {currentSceneIndex + 1} / {tour.scenes.length}
          </span>
        </div>
      )}

      {/* Instrucciones */}
      <div className="instructions">
        <p>Usa las flechas del teclado para navegar â€¢ Espacio para rotaciÃ³n automÃ¡tica â€¢ F para pantalla completa</p>
      </div>
    </div>
  );
}

export default EmbedViewer;
